// 컨트랙트 배포 방식
const LogicHelloWorldJSON = require('./Capsule.json');
var manage_contract_bytecode = LogicHelloWorldJSON.bytecode

var server_pk = new Buffer('6fe9b1e42a28dab325dcb950fda2718bf6c895e4084f3f5b73a9ee3eebfb4a37', 'hex');
var server_address = '0x916443f9f5A372200031918483f64250E6d47526';
web3.eth.getTransactionCount(server_address).then(function(nonce){
	return nonce
}).then(function(count){
	var rawTx = {nonce: 0, gasPrice: 20000000000, gasLimit: 6721975, data: manage_contract_bytecode};
	var tx = new Tx(rawTx);
	tx.sign(server_pk);
	var serializedTx = tx.serialize();
	web3.eth.sendSignedTransaction('0x' + serializedTx.toString('hex')).then(function(res){
		console.log(res);
	}).catch(function(err){
		console.log("error!!!");
		console.log(err);
	});
});
